import React, {Component} from 'react';
import './customers.css';

class Customers extends Component {
    constructor(props){
        super(props);
        this.state = {

            value:'',
            message:''

        }
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

   componentDidMount(){
       fetch('/api/customers')
        .then(res => res.json())
        .then(customers => this.setState({customers}, () => console.log('Customers fetched..',
        customers)));
   } 

   handleSubmit = (event) => {
       event.preventDefault()
       const data = this.state

       this.setState(
            { message : this.state.value,
             value:'' }
        );
        console.log(data)
   }

   handleInputChange = (event) =>{
        event.preventDefault()
        this.setState({
            value: event.target.value
        })
   }

  render(){  
      const{value, message} = this.state; 

    return (
        <div >
            <h1>Forms and Inputs</h1>

            <form onSubmit={this.handleSubmit}>
                <p><input type='text' placeholder = 'Your Message' value = {this.state.value}  onChange={this.handleInputChange} /></p>
                <p><button>Send Message</button></p>
            </form>
           <div id = 'message'> <p> Message is : {message}</p> </div>           
        </div>
      );
  }
}
export default Customers;
